import React from 'react';
import { Link } from 'react-router-dom';

const courses = [
  { id: 1, name: 'Air Hostesses', description: 'Training for aspiring air hostesses, focusing on service excellence.' },
  { id: 2, name: 'Flight Pursers', description: 'Comprehensive training for flight pursers, including leadership skills.' },
  { id: 3, name: 'Ticketing & Reservation', description: 'Learn the essentials of ticketing systems and reservation management.' },
  { id: 4, name: 'Customer Relations & Personality Development', description: 'Enhance skills in communication and relationship management.' },
];

const CoursesList = () => {
  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-center text-blue-600 mb-8">Aviation Courses</h1>
        
        <div className="course-list grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8 justify-center">
          {courses.map((course, index) => (
            <div
              key={course.id}
              className={`course-card p-6 bg-white rounded-lg shadow hover:shadow-xl transition-shadow ml ${
                index === 0 ? 'lg:col-start-2' : ''
              }`}
            >
              <h2 className="text-2xl font-bold mb-2 text-gray-800">{course.name}</h2>
              <p className="text-gray-600 mb-4">{course.description}</p>
              <Link to={`/course/${course.id}`} className="text-blue-500 hover:underline font-semibold">
                Learn More â†’
              </Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CoursesList;
